var i2c=global.get("i2c");
var i2c1 = null;
i2c1 = i2c.openSync(1);
var dataView     = new DataView(new ArrayBuffer(2));
var device = flow.get('device');
for (var ii = 0; ii < 64; ++ii)
{
    device.pixelTemp.value[0][ii] = ii;
    device.pixelTemp.value[1][ii] = 0.0;
}
var numAvgs = device.numAvgs.value;
for (var iavg = 0; iavg < numAvgs; ++iavg)
{
    for (var ii = 0; ii < 64; ++ii)
    {
        var byte1 = i2c1.readByteSync(0x69, 128 + 2 * ii);
        var byte2 = i2c1.readByteSync(0x69, 128 + 2 * ii + 1);
        dataView.setUint8(0, byte2);
        dataView.setUint8(1, byte1);
        var temperature = dataView.getInt16(0);
        if(temperature & (1 << 11))
        {
            temperature = ~temperature;
            temperature = temperature + 1;
            temperature = temperature & 4095;
            temperature = temperature * -1;
        }
        device.pixelTemp.value[1][ii] =  device.pixelTemp.value[1][ii] + (temperature  * 0.25);
    }
}
i2c1.closeSync();
for (var ii = 0; ii < 64; ++ii)
{
    device.pixelTemp.value[1][ii] = device.pixelTemp.value[1][ii] / numAvgs;
}
var writeCallData = false;
if (device.calTempOn.value == 1)
{
    for (var ii = 0; ii < 64; ++ii)
    {
        device.tempOffset.value[1][ii] = device.pixelTemp.value[1][ii] - device.calTemp.value;
    }
    writeCallData = true;
    device.calTempOn.value = 0;
}

device.pixelMin.value = 10000;
device.pixelMax.value = -10000;
for (var ii = 0; ii < 64; ++ii)
{
    device.pixelTemp.value[1][ii] = device.pixelTemp.value[1][ii] - device.tempOffset.value[1][ii];
    if (device.pixelMin.value > device.pixelTemp.value[1][ii]) device.pixelMin.value = device.pixelTemp.value[1][ii];
    if (device.pixelMax.value < device.pixelTemp.value[1][ii]) device.pixelMax.value = device.pixelTemp.value[1][ii];
}
device.pixelMin.value = Math.round(device.pixelMin.value * 10.0) / 10;
device.pixelMax.value = Math.round(device.pixelMax.value * 10.0) / 10;

if (writeCallData)
{
    return [{topic:'data',payload:device},{topic:'calData',payload:device}];
}
