function getPixelIndex(x, y)
{
    return (rawImageData.height - y) * rawImageData.width + x;
}
function getXY(ipt)
{
    var y = Math.floor(ipt / rawImageData.width);
    var x = ipt - y * rawImageData.width;
    y = rawImageData.height - 1 - y;
    return [x , y];
}
function getPixelTemp(ixTherm, iyTherm)
{
    if (ixTherm < 0) return -10000.0;
    if (ixTherm > 7) return -10000.0;
    if (iyTherm < 0) return -10000.0;
    if (iyTherm > 7) return -10000.0;
    return pixelTemp[ixTherm * 8 + iyTherm];
}

function getRGB(intensity)
{
  var rgb = [0,0,0];
  if ( (0 <= intensity) && (intensity <= 0.25) )
  {
    rgb[0] = 0;
    rgb[1] = Math.floor(255 * intensity * 4);    
    rgb[2] = 255;
  }
  if ( (0.25 < intensity) && (intensity <= 0.50) )
  {
    rgb[0] = 0;
    rgb[1] = 255;    
    rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
  }
  if ( (0.50 < intensity) && (intensity <= 0.75) )
  {
    rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
    rgb[1] = 255;    
    rgb[2] = 0;
  }
  if ( (0.75 < intensity) && (intensity <= 1.0) )
  {
    rgb[0] = 255;
    rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
    rgb[2] = 0;
  }
  return rgb;
}

